package com.akp.Employee.controller;import com.akp.Employee.entity.Employee;import com.akp.Employee.mapper.EmployeeMapper;import com.akp.Employee.model.EmployeeDTO;import com.akp.Employee.service.EmployeeService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping(value = "/employee")public class EmployeeController {    @Autowired    private EmployeeService employeeService;    @PostMapping(value = "/save", consumes = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Employee> saveEmployeeDetails(@RequestBody EmployeeDTO employeeDTO){        Employee employee;        employee = new EmployeeMapper().toEntity(employeeDTO);        return new ResponseEntity<>(employeeService.saveEmployee(employee), HttpStatus.CREATED);    }    @GetMapping(value = "/get", produces = MediaType.APPLICATION_JSON_VALUE)    public List<Employee> getAllEmployee(){        return employeeService.getAllEmployee();    }    @GetMapping(value = "/get/{id}", produces = MediaType.APPLICATION_JSON_VALUE)    public Employee getEmployee(@PathVariable("id") int id){        return employeeService.getEmployee(id);    }    @PutMapping(value = "/update/{id}")    public ResponseEntity<Employee> updateEmployee(@RequestBody EmployeeDTO employeeDTO, @PathVariable int id){        Employee employee;        employee = new EmployeeMapper().toEntity(employeeDTO);        return new ResponseEntity<>(employeeService.updateEmployee(employee, id), HttpStatus.ACCEPTED);    }    @DeleteMapping(value = "/delete/{id}")    public ResponseEntity<Long> deleteEmployee(@PathVariable("id") int id){        return new ResponseEntity<>(employeeService.deleteEmployee(id), HttpStatus.ACCEPTED);    }}