package com.akp.Employee.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;@Data@AllArgsConstructor@NoArgsConstructor@Entity@Builder@Table(name = "Employee")public class Employee {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    @Column(name = "Employee_ID")    private int empId;    @Column(name = "Employee_Name")    private String empName;    @Column(name = "Employee_Age")    private int empAge;    @Column(name = "Employee_Department")    private String department;}