package com.akp.Employee.error;import com.akp.Employee.model.ErrorResponse;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.servlet.mvc.method.annotation.ResponseEntityExceptionHandler;@ControllerAdvicepublic class EmployeeExceptionHandler extends ResponseEntityExceptionHandler {    @ExceptionHandler(EmployeeNotFoundException.class)    public ResponseEntity<ErrorResponse> handleEmployeeNotFoundException(EmployeeNotFoundException e){        ErrorResponse errorResponse = ErrorResponse.builder()                .message(e.getMessage())                .statusCode(e.getErrorCode())                .build();        return new ResponseEntity<>(errorResponse, HttpStatus.NOT_FOUND);    }}