package com.akp.Employee.mapper;import com.akp.Employee.entity.Employee;import com.akp.Employee.model.EmployeeDTO;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;public class EmployeeMapper {    public Employee toEntity(EmployeeDTO employeeDTO){       /* Employee employee = new Employee();        employee.setEmpName(employeeDTO.getEmpName());        employee.setDepartment(employeeDTO.getDepartment());        employee.setEmpAge(employeeDTO.getEmpAge());        return employee;*/        Employee employee = Employee.builder()                .empName(employeeDTO.getEmpName())                .department(employeeDTO.getDepartment())                .empAge(employeeDTO.getEmpAge())                .build();        return employee;    }    public EmployeeDTO toDTO(Employee employee){        /*EmployeeDTO employeeDTO = new EmployeeDTO();        employeeDTO.setEmpName(employee.getEmpName());        employeeDTO.setDepartment(employee.getDepartment());        employeeDTO.setEmpAge(employee.getEmpAge());        return employeeDTO;*/        EmployeeDTO employeeDTO = EmployeeDTO.builder()                .empName(employee.getEmpName())                .department(employee.getDepartment())                .empAge(employee.getEmpAge())                .build();        return employeeDTO;    }}